function FullScreenClicked()
{
    parent.document.documentElement.requestFullscreen();
}

function ExitFullScreenClicked()
{
    parent.document.exitFullscreen();
}


function FullScreenToggle()
{
	elem = parent.document.documentElement;

	if (! parent.document.fullscreenElement && !parent.document.webkitFullscreenElement ) {
		//original if (!document.fullscreenElement && !document.mozFullScreenElement && !document.webkitFullscreenElement && !document.msFullscreenElement) {
		if (elem.requestFullscreen) {
			elem.requestFullscreen();
		} else if (elem.msRequestFullscreen) {
			elem.msRequestFullscreen();
		} else if (elem.mozRequestFullScreen) {
			elem.mozRequestFullScreen();
		} else if (elem.webkitRequestFullscreen) {
			elem.webkitRequestFullscreen(Element.ALLOW_KEYBOARD_INPUT);
		}
	} else {
		if (parent.document.fullscreenElement) {
			parent.document.exitFullscreen();
		//if (document.exitFullscreen) {
		//	document.exitFullscreen();

		} else if (parent.document.msExitFullscreen) {
			parent.document.msExitFullscreen();
		//} else if (document.msExitFullscreen) {
		//	document.msExitFullscreen();

		} else if (parent.ducument.mozCancelFullScreen) {
			parent.document.mozCancelFullScreen();
		//} else if (document.mozCancelFullScreen) {
		//	document.mozCancelFullScreen();

		} else if (parent.document.webkitExitFullscreen) {
			parent.ducument.webkitExitFullscreen();
		//} else if (document.webkitExitFullscreen) {
		//	document.webkitExitFullscreen();
		}
	}

}


